  },
  fab: {
    position: "absolute",
    margin: 16,
    right: 0,
    bottom: 0,
    backgroundColor: "green",
  },
  checkbox: {
    alignSelf: "center",
    height: 22,
    width: 22,
  },
  label: {
    margin: 8,
  },
});
export default ViewAllMembers;
