import React, {useEffect} from "react";
import { Text, View } from "react-native";
import { FAB, TextInput } from "react-native-paper";
import { useForm, Controller } from "react-hook-form";
import firebase from "firebase";
import { auth, db } from "../../firebase";
import styles from "../../styles";
import {Picker} from '@react-native-picker/picker';

const AddOneCommittee = ({ navigation }) => {

  const [AllMembers, setAllMembers] = React.useState([]);
  const [MembersForPicker, setMembersForPicker] = React.useState([]);

const memberCommitteeDetail=[];
const onChangePicker=(e)=>
{
  let memberName = e.target.value;
    let monthPaid = new Array(12).fill(false);
    // memberCommitteeDetail.push({
    //   memberName: memberName,
    //   monthPaid: monthPaid,
      
    // });

    // console.log("in setfieldprp", this.state.memberMonths);
  }

    useEffect(() => {
        

          const refPicker = db.collection("members");
        
        refPicker.onSnapshot((query) => {
          const objs = [];
          query.forEach((doc) => {
            objs.push({
              id: doc.id,
              ...doc.data(),
            });
          });
          console.log("objs at add member",objs)
          setAllMembers(objs);
        })
      }, []);
  const {
    control,
    handleSubmit,
    formState: { errors },
  } = useForm();

  const onSubmit = async (data) => {
    const user = auth.currentUser;
    try {
      console.log("The date is", data);
      const committee = {
        comName: data.nameofcommittee,
        comShare: data.committeeshare,
        completed: false,
        belongsTo: user.uid,
        createdAt: firebase.firestore.FieldValue.serverTimestamp(),
      };

      const ref = db.collection("committees");

      var addedCommitteeId = await ref.add(committee);
      var junctionRel = { com_id: addedCommitteeId.id, member_id: user.uid
    , Jan: false , Feb: false, Mar: false, Apr: false , May: false
    , Jun: false , Jul: false , Aug: false , Sep: false, Oct: false, Nov: false
        ,Dec: false };
      const refJunction = db.collection("com_member_rel");
      await refJunction.add(junctionRel);

      navigation.goBack();
    } catch (err) {
      console.log(err);
    }
  };

  console.log("The memberforpicker value before return()", AllMembers)

  return (
    <>
      <Picker
  selectedValue={MembersForPicker.memberName}
  onValueChange={() =>
    onChangePicker
  }>
{AllMembers && AllMembers.map((x)=>{ 
  <Picker.Item label="MemberName" value={x.id} >{ x.memberName}</Picker.Item>
})}
</Picker>
      <FAB style={styles.fab} icon="plus" onPress={handleSubmit(onSubmit)} />
      
    </>
  );
};
export default AddOneCommittee;
