import React, {useRef} from 'react'
import { View, Text } from 'react-native'
import { useForm, Controller } from 'react-hook-form'
import { TextInput, Button  } from 'react-native-paper'
import {auth} from '../firebase'
import styles from './styles';
const CommitteeDetailByMembers =({route})=>{
    const { control, handleSubmit, formState: { errors } } = useForm();
const{comName, comShare, comId}=route.params

console.log("the nav",comName, comShare, comId)
return (
    <>

    <Controller
    control={control}
    rules={{
     required: true,
    }}
    render={({ field: { onChange, onBlur, value } }) => (
      <TextInput
        // style={styles.input}
        onBlur={onBlur}
        // onChangeText={onChange}
        value={comName}
      />
    )}
    name="comName"
    defaultValue=""
  />
  <Controller
  label="Committee Name"
  mode="outlined"
    control={control}
    rules={{
     required: true,
    }}
    render={({ field: { onChange, onBlur, value } }) => (
      <TextInput
         style={styles.inputatcomdetail}
        onBlur={onBlur}
        // onChangeText={onChange}
        value={comShare}
      />
    )}
    name="comShare"
    defaultValue=""
  />
</>
)



}

export default CommitteeDetailByMembers;
