          itemsPerPage={itemsPerPage}
          setItemsPerPage={setItemsPerPage}
          showFastPagination
          optionsLabel={"Rows per page"}
        />
      </View>
      <FAB
        style={styles.fab}
        label="Add Committee"
        onPress={() => navigation.navigate("addOnecommittee")}
      />
    </>
  );
};

export default ViewAllCommittees;
