import React from "react";
import { Text, View , Picker} from "react-native";
import { FAB, TextInput } from "react-native-paper";
import { useForm, Controller } from "react-hook-form";
import firebase from "firebase";
import { auth, db } from "../../firebase";
import styles from "../../styles";
import RNPickerSelect, { defaultStyles } from 'react-native-picker-select';

const AddOneCommittee = ({ navigation }) => {
  const {
    control,
    handleSubmit,
    formState: { errors },
  } = useForm();

  const onSubmit = async (data) => {
    const user = auth.currentUser;
    try {
      console.log("The date is", data);
      const committee = {
        comName: data.nameofcommittee,
        comShare: data.committeeshare,
        completed: false,
        belongsTo: user.uid,
        createdAt: firebase.firestore.FieldValue.serverTimestamp(),
      };

      const ref = db.collection("committees");

      var addedCommitteeId = await ref.add(committee);
      var junctionRel = { com_id: addedCommitteeId.id, member_id: user.uid
    , Jan: false , Feb: false, Mar: false, Apr: false , May: false
    , Jun: false , Jul: false , Aug: false , Sep: false, Oct: false, Nov: false
        ,Dec: false };
      const refJunction = db.collection("com_member_rel");
      await refJunction.add(junctionRel);

      navigation.goBack();
    } catch (err) {
      console.log(err);
    }
  };
  return (
    <>
      <Picker
        selectedValue={selectedValue}
        style={{ height: 50, width: 150 }}
        onValueChange={(itemValue, itemIndex) => setSelectedValue(itemValue)}
      >
        <Picker.Item label="Java" value="java" />
        <Picker.Item label="JavaScript" value="js" />
      </Picker>
      <FAB style={styles.fab} icon="plus" onPress={handleSubmit(onSubmit)} />
      
    </>
  );
};
export default AddOneCommittee;
