import React, {useRef, useState, useEffect} from 'react'
import { View, Text } from 'react-native'
import { useForm, Controller } from 'react-hook-form'
import { TextInput, Button  } from 'react-native-paper'
import {auth, db} from '../../firebase'
import styles from '../../styles';
import { DataTable, FAB, List } from 'react-native-paper';

const CommitteeDetailByMembers =({route, navigation})=>{
const optionsPerPage = [2, 3, 4];
const [com_member_rel, setCom_member_rel]=useState([])

    const { control, handleSubmit, formState: { errors } } = useForm();
//  const{comName, comShare, comId}=route.params
 const [page, setPage] = useState(0);
 const [itemsPerPage, setItemsPerPage] = React.useState(optionsPerPage[0]);

const abc=[];
 console.log("the nav",route.params);
 useEffect(() => {
  const ref = db.collection("com_member_rel");
  ref.onSnapshot((query) => {
    const objs = [];
    query.forEach((doc) => {
      objs.push({
        id: doc.id,
        ...doc.data(),
      });
    });
    setCom_member_rel(objs);
  });
}, []);
return (
    <>
<View>
    {/* <Controller
    control={control}
    rules={{
     required: true,
    }}
    render={({ field: { onChange, onBlur, value } }) => (
      <TextInput
       style={styles.inputatcomdetail1}
        onBlur={onBlur}
       onChangeText={onChange}
        value={comName}
      />
    )}
    name="comName"
    defaultValue=""
  />
  <Controller
  label="Committee Name"
  mode="flat"
    control={control}
    rules={{
     required: true,
    }}
    render={({ field: { onChange, onBlur, value } }) => (
      <TextInput
         style={styles.inputatcomdetail}
        onBlur={onBlur}
        onChangeText={onChange}
        value={comShare}
      />
    )}
    name="comShare"
    defaultValue=""
  />
   */}
<DataTable.Header>
        <DataTable.Title>Member Name</DataTable.Title>
        <DataTable.Title >Jan</DataTable.Title>
        <DataTable.Title >Feb</DataTable.Title>
        <DataTable.Title >Mar</DataTable.Title>
        <DataTable.Title >Apr</DataTable.Title>
        <DataTable.Title >May</DataTable.Title>
        <DataTable.Title >Jun</DataTable.Title>
        <DataTable.Title >Jul</DataTable.Title>
        <DataTable.Title >Aug</DataTable.Title>
        <DataTable.Title >Sep</DataTable.Title>
        <DataTable.Title >Oct</DataTable.Title>
        <DataTable.Title >Nov</DataTable.Title>
        <DataTable.Title >Dec</DataTable.Title>
        
      </DataTable.Header>
    {abc.map((x)=>(
    <View key={x} >
      
        <DataTable >
      
      {/* <List.Items onPress={()=> handleSubmit(x)}> */}
      
      <DataTable.Row  >
      
        <DataTable.Cell ></DataTable.Cell>
        <DataTable.Cell boolean>
          <CheckBox
          value=""
          onValueChange=""
          
        /></DataTable.Cell>
        
      </DataTable.Row>
      

      

       
    </DataTable>
    </View>))}
    <DataTable.Pagination
        page={page}
        numberOfPages={3}
        onPageChange={(page) => setPage(page)}
        label="1-2 of 6"
        optionsPerPage={optionsPerPage}
        itemsPerPage={itemsPerPage}
        setItemsPerPage={setItemsPerPage}
        showFastPagination
        optionsLabel={'Rows per page'}
      /> 
</View>
<FAB
style={styles.fab}
    icon="plus"
    onPress={ ()=> navigation.navigate('addmember')}
    />
       
</>
)



}

export default CommitteeDetailByMembers;
