import React from 'react';
import {Text , View} from 'react-native';
import { FAB, TextInput } from 'react-native-paper';
import { useForm, Controller } from 'react-hook-form';
import firebase from 'firebase'
import { auth, db } from '../../firebase';


const AddOneCommittee=({navigation})=>
{

const { control , handleSubmit , formState:{errors}}= useForm();

const onSubmit = async (data)=>
{
    const user= auth.currentUser;
    try{
        const committee ={
            name: data.shopminder,
            completed: false,
            belongsTo: user.uid,
            createdAt: firebase.firestore.FieldValue.serverTimestamp()
        }

        const ref= db.collection('shopminder');

        await ref.add(shopminder);
        navigation.goBack();
            }
            catch(err){
                console.log(err)
            }
}
    return(
        <>
        <Controller 
        control={control}
        render={({field:{onChange, onBlur, value}})=>
    (
        <TextInput
        label= "Name of Committee"
        mode='outlined'
        onBlur={onBlur}
        onChangeText={onChange}
        value={value}
        />
        )}
        name="nameofcommittee"
        rules={{ required: true}}
        defaultValue=""
        />
        <View>
            {errors.nameofcommittee && <Text> Enter Committee Name</Text>}
        </View>
        <FAB
        icon="plus"
        onPress= {handleSubmit(onSubmit)}
        />
        </>
    )
}
export default AddOneCommittee;
