import React, {useState, useEffect} from 'react'
import {Text, View} from 'react-native'
import { db, auth } from '../firebase'

const CommitteesTab =()=>{
    const [committees, setCommittees]=useState([])
    useEffect(() => {
        const ref = db.collection("committees");
        ref.onSnapshot((query) => {
          const objs = [];
          query.forEach((doc) => {
            objs.push({
              id: doc.id,
              ...doc.data(),
            });
          });
          setCommittees(objs);
        });
      }, []);

return (
<View>
    {committees.map((x)=>
    <View key={x.id}>
        <DataTable>
      <DataTable.Header>
        <DataTable.Title>Committee Name</DataTable.Title>
        <DataTable.Title numeric>Committee Share</DataTable.Title>
      </DataTable.Header>

      <DataTable.Row>
        <DataTable.Cell>{x.comName}</DataTable.Cell>
        <DataTable.Cell numeric>159</DataTable.Cell>
        <DataTable.Cell numeric>6.0</DataTable.Cell>
      </DataTable.Row>

      

      <DataTable.Pagination
        page={page}
        numberOfPages={3}
        onPageChange={(page) => setPage(page)}
        label="1-2 of 6"
        optionsPerPage={optionsPerPage}
        itemsPerPage={itemsPerPage}
        setItemsPerPage={setItemsPerPage}
        showFastPagination
        optionsLabel={'Rows per page'}
      />
    </DataTable>
        {x.comName}
        {x.comShare}
    </View>)}
</View>

)



}

export default CommitteesTab;
