import { StatusBar } from "expo-status-bar";
import React, { useState, useEffect } from "react";
import { StyleSheet, Text, View, SafeAreaView } from "react-native";
import { db } from "./firebase";
import { auth } from "./firebase";
import { NavigationContainer, DefaultTheme } from "@react-navigation/native";
import { createStackNavigator } from "@react-navigation/stack";
import LoginScreen from "./screens/sessions/LoginScreen";
import RegisterScreen from "./screens/sessions/RegisterScreen";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import { FontAwesome } from "@expo/vector-icons";
import MembersTab from "./CommitteeMembers/MembersTab";
import SettingsTab from "./SafeAreaView/SettingsTab";

const Stack = createStackNavigator();
const Tab = createBottomTabNavigator();

export default function App() {
  const [members, setMembers] = useState([]);
  const [signedIn, setSignedIn] = useState(false);
  auth.onAuthStateChanged((user) => {
    if (user) {
      setSignedIn(true);
    } else {
      setSignedIn(false);
    }
  });

  useEffect(() => {
    const ref = db.collection("savekarlo");
    ref.onSnapshot((query) => {
      const objs = [];
      query.forEach((doc) => {
        objs.push({
          id: doc.id,
          ...doc.data(),
        });
      });
      setMembers(objs);
    });
  }, []);
  return (
    <NavigationContainer theme={DefaultTheme}>
      {signedIn ? (
        <SafeAreaView style={{flex: 1, backgroundColor: '#29434e'}}>
        <Tab.Navigator
          screenOptions={({ route }) => ({
          tabBarIcon: ({ color, size }) => {
            if (route.name === 'committee') {
              return (
                <FontAwesome 
                  name='list-ul'
                  size={size}
                  color={color}
                />
              )
            } 
            if (route.name === 'settings') {
              return (
                <FontAwesome 
                  name="cogs"
                  size={size}
                  color={color}
                />
              )
            }
          },
        })}
        tabBarOptions={{
          activeTintColor: 'white',
          inactiveTintColor: '#819ca9',
          style: {
            backgroundColor: '#29434e'
          }
        }}
        >
          <Tab.Screen 
            name="shopminders"
            component={CommitteesTab}
            options={{
              title: 'Shopminders'
            }}
          />
          <Tab.Screen 
            name="settings"
            component={MembersTab}
            options={{
              title: 'Settings'
            }}
           />
         </Tab.Navigator>
      </SafeAreaView>
      ) : (
        <>
          <StatusBar style="light" />
          <Stack.Navigator mode="card" screenOptions={{}}>
            <Stack.Screen
              name="signIn"
              component={LoginScreen}
              options={{
                title: "Sign In",
                // header:'Sign In',
                headerStyle: {
                  backgroundColor: "#29434e",
                  borderBottomColor: "#29434e",
                },
                headerTintColor: "#fff",
              }}
            />
            <Stack.Screen
              name="register"
              component={RegisterScreen}
              options={{
                title: "Register",
                headerStyle: {
                  backgroundColor: "#29434e",
                  borderBottomColor: "#29434e",
                },
                headerTintColor: "#fff",
              }}
            />
          </Stack.Navigator>
        </>
      )}
    </NavigationContainer>
  );
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },
});
